package gameclient;import java.io.IOException;import java.net.ServerSocket;import java.nio.ByteBuffer;public class PortMigrationRequest {    private int Port;    private ClientConnector ClientConnector;    public PortMigrationRequest(ClientConnector cc) {        super();        this.ClientConnector = cc;    }    public void Deserialize(byte[] bufr) {        ByteBuffer bb = ByteBuffer.wrap(new byte[] { bufr[2], bufr[3], bufr[4], bufr[5] });        int i = bb.getInt();        this.Port = i;        Migrate();    }    public void Migrate() {        try {            ServerSocket ss = new ServerSocket(this.Port);            this.ClientConnector.removeSocket(ClientConnector.getSockets().get(0));            this.ClientConnector.addSocket(ss.accept());            System.out.println("Accepted connection on port " + Port);        } catch (IOException ioe) {            ioe.printStackTrace();        }    }}